DROP TABLE MAQUINARIA;
DROP TABLE PROVEEDORES;

CREATE TABLE MAQUINARIA (
    ID              SMALLSERIAL,
    NUMERO          SMALLINT,
    DESCRIPCION     VARCHAR,
    MARCA           VARCHAR NOT NULL,
    MODELO          VARCHAR,
    NSERIE          VARCHAR PRIMARY KEY,
    PERIODO         VARCHAR,
    ACTIVO_FIJO     VARCHAR,
    ZONA            VARCHAR,
    PLANTA          VARCHAR(1),
    FECHA_COMPRA    DATE,
    EN_USO          BOOLEAN,
    EN_SERVICIO     BOOLEAN,
    CAUSA           VARCHAR
);

CREATE TABLE PROVEEDORES (ID SMALLSERIAL PRIMARY KEY, NOMBRE VARCHAR, COMPANIA VARCHAR);

